            assetChainId  INT      not null,
            assetId  INT      not null,
            amount  BIGINT      not null,
            state   TINYINT     not null,
            time   BIGINT      not null
            );
            create index IF NOT EXISTS ${tx.indexName}
            on ${tx.tableName} (address, time, type);
